};

sp_widget.css:
.text-center button {
  background-color: #0078d7;       /* Brand blue */
  color: #fff;                     /* White text */
  font-size: 20px;                 /* Larger text */
  font-weight: 600;                /* Semi-bold */
  padding: 16px 40px;              /* Big button size */
  border: none;                    /* Remove border */
  border-radius: 12px;             /* Rounded corners */
  cursor: pointer;                 /* Hand cursor */
  transition: all 0.3s ease;       /* Smooth hover animation */
  box-shadow: 0 6px 12px rgba(0,0,0,0.15); /* Soft shadow */
}

/* Hover effect */
.text-center button:hover {
  background-color: #005a9e;       /* Darker blue */
  transform: translateY(-2px);     /* Slight lift */
  box-shadow: 0 8px 16px rgba(0,0,0,0.25);
}

/* Active (click) effect */
.text-center button:active {
  transform: translateY(0);        /* Pressed down */
  box-shadow: 0 4px 8px rgba(0,0,0,0.2);
}

sp_widget.link:
function link(scope, element, attrs, controller) {
  element.find('.create-task-btn').on('click', function() {
    var url = "/sp?id=form&table=u_community_maintenance_task&sys_id=-1";
    window.open(url, "_self"); // opens inside Service Portal
  });
}
