sys_script.script:
(function executeRule(current, previous /*null when async*/) {
    // Get the assignment group/role for the task
    var taskRole = current.assignment_group.getDisplayValue(); // or current.role if that's your field name

    // Match only if Role is one of these (you can adjust the list)
    var targetRoles = ['Plumber', 'Electrician', 'Carpenter'];
    if (targetRoles.indexOf(taskRole) === -1) {
        return;
    }

    // Query your staff table (replace 'staff_profile' if using different table name, e.g. 'x_gcvpms_staff')
    var staffGR = new GlideRecord('staff_profile'); // <--- Use your staff table's actual name!
    staffGR.addQuery('role', taskRole);
    staffGR.addQuery('state', 'Available');
    staffGR.query();

    if (staffGR.next()) {
        current.allocated_to = staffGR.sys_id;
    } else {
        current.allocated_to = '';
    }
})(current, previous);
