sp_widget.client_script:
function abs($scope, $interval) {
  $scope.newMessage = '';
  $scope.pollMs = $scope.data.poll_ms || 3000;

  function refresh(convId) {
    return $scope.server.update({
      conversation_id: convId || $scope.data.conversationId
    }).then(function() {
      setTimeout(function() {
        var el = document.getElementById('chat-messages');
        if (el) el.scrollTop = el.scrollHeight;
      }, 50);
    });
  }

  $scope.sendMessage = function() {
    if (!$scope.newMessage || !$scope.newMessage.trim()) return;
    $scope.server.update({
      action: 'send',
      new_message: $scope.newMessage,
      conversation_id: $scope.data.conversationId,
      to_user: $scope.data.to_user
    }).then(function() {
      $scope.newMessage = '';
      refresh();
    });
  };

  // Poll
  var poller = $interval(function() {
    if ($scope.data.conversationId) refresh();
  }, $scope.pollMs);

  $scope.$on('$destroy', function() {
    if (angular.isDefined(poller)) $interval.cancel(poller);
  });

  // Initial
  if ($scope.data.conversationId) refresh();
}
