sp_widget.link:
function link(scope, element, attrs, controller) {
    scope.$watch('showResidents', function(newVal) {
        if (newVal) {
            element.find('button').text('Hide Staff Profiles ');
        } else {
            element.find('button').text('Show Staff Profiles');
        }
    });
}

sp_widget.script:
(function() {
    data.residents = [];

    var gr = new GlideRecord('u_staff_profile'); // your table
    gr.query();
    while (gr.next()) {
        data.residents.push({
            sys_id: gr.getUniqueValue(),
            name: gr.getDisplayValue('u_user'),       // FIXED
            address: gr.getDisplayValue('u_role'), // FIXED
            phone: gr.getDisplayValue('state')          // FIXED
        });
    }
})();
