sys_script.script:
(function executeRule(current, previous /*null when async*/) {
    if (!current.u_conversation) {
        var conv = new GlideRecord('u_conversation');
        conv.initialize();
        conv.u_resident = gs.getUserID();

        // Example: Find assigned staff for this resident
        var res = new GlideRecord('u_community_maintenance_task');
        res.addQuery('u_resident', gs.getUserID());
        res.query();
        if (res.next()) {
            conv.u_staff = res.u_staff; 
        } else {
            // fallback: assign default staff
            conv.u_staff = 'staff_sys_id_here';
        }

        conv.u_status = 'Active';
        var convID = conv.insert();
        current.u_conversation = convID;
    }
})(current, previous);
